step.params:
output = JSON.parse(value).map(function(dev){
    return {
        "{#DEVNAME}": dev.Name,
        "{#DEVQUEUE}": dev.CurrentDiskQueueLength,
        "{#DEVREADS}": dev.DiskReadsPersec,
        "{#DEVTIME}": dev.PercentDiskTime,
        "{#DEVWRITES}": dev.DiskWritesPersec
    }})
return JSON.stringify({"data": output})
